define('text!samples/meteorites/meteorites.js',[],function () { return 'var squareSize = 15;\nvar spawnPulse = 1000;\nvar fallPulse = 200;\nvar firingPulse = 50;\n\napi.initGrid(squareSize);\n\nvar meteoriteLayer = api.addLayer(\'#275b8c\');\nvar bulletLayer = api.addLayer(\'#9bc2e3\');\nvar spaceshipLayer = api.addLayer(\'#337ab7\');\n\n// ship starting position\nspaceshipLayer.fill({x: api.randomCoord(), y: api.gameSize - 1});\n\n// meteorite spawns\nRx.Observable.interval(spawnPulse)\n    .map(() => ({x: api.randomCoord(), y: 0}))\n    .subscribe(meteoriteLayer.fill);\n\n// meteorite updates\nRx.Observable.interval(fallPulse)\n    .flatMap(() => Rx.Observable.from(meteoriteLayer.getActiveSquares()))\n    .do(meteoriteLayer.clear)\n    .map(api.directions.Down)\n    .do(meteoriteLayer.fill)\n    .where(api.isOffLimits)\n    .subscribe(api.gameOver);\n\n// bullet spawns\napi.keyboard.filter(keyCode => keyCode == 32)\n    .map(() => spaceshipLayer.getActiveSquares()[0])\n    .subscribe(bulletLayer.fill);\n\n// bullet updates\nRx.Observable.interval(firingPulse)\n    .flatMap(() => Rx.Observable.from(bulletLayer.getActiveSquares()))\n    .do(bulletLayer.clear)\n    .map(api.directions.Up)\n    .where(api.isWithinLimits)\n    .subscribe(bulletLayer.fill);\n\n// spaceship moves\napi.keyboard.filter(keyCode => _.contains([37, 39], keyCode))\n    .map(key => api.directions[key](spaceshipLayer.getActiveSquares()[0]))\n    .filter(api.isWithinLimits)\n    .do(spaceshipLayer.fill)\n    .subscribe(() => spaceshipLayer.clear(spaceshipLayer.getActiveSquares()[0]));\n\n// hit detection\nvar hit = bulletLayer.activations\n    .map(bullet => ({\n        bullet: bullet,\n        meteorite: meteoriteLayer.getActiveSquares()\n            .find(meteorite => meteorite.x == bullet.x && meteorite.y >= bullet.y)\n    }))\n    .filter(hit => hit.meteorite);\n\n// asset destruction & scoring\nvar score = 1;\nhit.subscribe(hit => {\n    api.setText(\'Score : \' + score++);\n    meteoriteLayer.clear(hit.meteorite);\n    bulletLayer.clear(hit.bullet);\n});';});


define('text!samples/meteorites/README.md',[],function () { return 'Meteorites are falling from the sky.\n\nMake sure none reach the ground.\n\n**How to play**\n\nUse the Left and Right arrows of your keyboard to aim.\n\nUse the Space bar to fire.';});


define('text!samples/snake/snake.js',[],function () { return 'var snakeSpeedPulse = 60;\nvar squareSize = 15;\n\napi.initGrid(squareSize);\n\nvar snake = api.addLayer(\'#337ab7\');\nvar food = api.addLayer(\'#9bc2e3\');\n\nsnake.fill({x: 1, y: 0}).fill({x: 1, y: 1}); // snake starting point\n\n// duplicate the last direction at fixed intervals\nvar validKeystrokes = api.keyboard.filter(keyCode => keyCode in api.directions);\nvar directions = Rx.Observable\n    .interval(snakeSpeedPulse)\n    .withLatestFrom(validKeystrokes, (p, k) => k);\n\n// detect snake-food collisions\nvar eaten = food.activations\n    .flatMapLatest(foodCoord => snake.activations.filter(_.matcher(foodCoord)));\n\n// add flag \'grow: true\' when snake should grow\nvar updates = directions\n    .window(eaten)\n    .flatMap((directions, i) => directions.map((key, j) => ({key: key, grow: i && !j})));\n\n// update snake position and size, trigger end-of-game\nupdates.subscribe(update => {\n\n    var activeSquares = snake.getActiveSquares();\n    var head = api.directions[update.key](_.last(activeSquares));\n    if (api.isOffLimits(head) || _.findWhere(activeSquares, head)) api.gameOver();\n    if (!update.grow) snake.clear(snake.getActiveSquares()[0]);\n    snake.fill(head);\n});\n\n// update food\neaten.do(food.clear).subscribe(() => food.fill(api.randomSquare()));\n\nfood.fill(api.randomSquare());// first helping of food';});


define('text!samples/snake/README.md',[],function () { return 'The classical mobile game inspired by [A Playful Introduction to Rx by Erik Meijer](https://youtu.be/WKore-AkisY).\n\nThe original code written in Scala can be found [on slide 4](http://ecoop14.it.uu.se/programme/Rx.pdf).\n\n**How to play**\n\nUse the Left, Up, Right and Down arrows of your keyboard to help the snake catch some food.\n\nIt\'s game over when the snake walks over itself or leaves the screen.\n\n\n**Limitations**\n\n - end of game is not implemented\n - food can appear on the snake';});


define('text!samples/rain-using-state/rain-using-state.js',[],function () { return 'var squareSize = 6;\nvar spawnPulse = 200;\nvar fallPulse = 100;\n\napi.initGrid(squareSize);\n\nvar layer = api.addLayer(\'#337ab7\');\n\n// droplets spawns\nRx.Observable.interval(spawnPulse)\n    .map(() => ({x: api.randomCoord(), y: 0}))\n    .subscribe(layer.fill);\n\n// droplets updates\nRx.Observable.interval(fallPulse)\n    .flatMap(() => Rx.Observable.from(layer.getActiveSquares()))\n    .do(layer.clear)\n    .map(api.directions.Down)\n    .where(api.isWithinLimits)\n    .subscribe(layer.fill);';});


define('text!samples/rain-using-state/README.md',[],function () { return 'This snippet makes particles fall from the sky as in the\n[Meteorites game](https://julienmoumne.github.io/rx-training-games/#?title=meteorites).\n\nThis approach is the most concise because it relies on shared state in the form of an \nObservable Collection.\nSee the [API documentation](https://github.com/JulienMoumne/rx-training-games/blob/master/API.md) for more details.';});


define('text!samples/rain-using-scan/rain-using-scan.js',[],function () { return 'var squareSize = 6;\nvar spawnPulse = 200;\nvar fallPulse = 100;\n\napi.initGrid(squareSize);\n\nvar layer = api.addLayer(\'#337ab7\');\n\nvar droplets = Rx.Observable.interval(spawnPulse)\n    .map(() => ({current: {x: api.randomCoord(), y: 0}}))\n    .flatMap(startingPosition => Rx.Observable.interval(fallPulse).scan(\n        coord => ({\n            current: api.directions.Down(coord.current),\n            previous: coord.current\n        }),\n        startingPosition\n    )\n    .startWith(startingPosition)\n    .takeWhile(\n        coord => !coord.previous || api.isWithinLimits(coord.previous))\n    );\n\ndroplets.subscribe(coord => {\n    if (api.isWithinLimits(coord.current)) layer.fill(coord.current);\n    if (coord.previous) layer.clear(coord.previous);\n});';});


define('text!samples/rain-using-scan/README.md',[],function () { return 'This snippet makes particles fall from the sky as in the\n[Meteorites game](https://julienmoumne.github.io/rx-training-games/#?title=meteorites).\n\nThis approach leverages the\n[Scan Operator](https://github.com/Reactive-Extensions/RxJS/blob/master/doc/api/core/operators/scan.md)\nand does not rely on on shared state.\n\nIt revolves around the following structure : \n```javascript\n{\n    previous : { x: xValue, y: yValue },\n    next : { x: xValue, y: yValue }\n}\n```';});


define('text!samples/rain-using-generate/rain-using-generate.js',[],function () { return 'var squareSize = 6;\nvar spawnPulse = 200;\nvar fallPulse = 100;\n\napi.initGrid(squareSize);\n\nvar layer = api.addLayer(\'#337ab7\');\n\nvar droplets = Rx.Observable.interval(spawnPulse)\n    .flatMap(() => Rx.Observable.generateWithRelativeTime(\n        {current: {x: api.randomCoord(), y: 0}},\n        coord => !coord.previous || api.isWithinLimits(coord.previous),\n        coord => ({\n            current: api.directions.Down(coord.current),\n            previous: coord.current\n        }),\n        _.identity,\n        _.constant(fallPulse)\n    ));\n\ndroplets.subscribe(coord => {\n    if (api.isWithinLimits(coord.current)) layer.fill(coord.current);\n    if (coord.previous) layer.clear(coord.previous);\n});';});


define('text!samples/rain-using-generate/README.md',[],function () { return 'This snippet makes particles fall from the sky as in the\n[Meteorites game](https://julienmoumne.github.io/rx-training-games/#?title=meteorites).\n\nThis approach leverages the\n[GenerateWithAbsoluteTime Operator](https://github.com/Reactive-Extensions/RxJS/blob/master/doc/api/core/operators/generatewithabsolutetime.md)\nand does not rely on on shared state.\n\nIt revolves around the following structure : \n```javascript\n{\n    previous : { x: xValue, y: yValue },\n    next : { x: xValue, y: yValue }\n}\n```';});


define('text!samples/rain-using-generator/rain-using-generator.js',[],function () { return 'var squareSize = 6;\nvar spawnPulse = 200;\nvar fallPulse = 100;\n\napi.initGrid(squareSize);\n\nvar layer = api.addLayer(\'#337ab7\');\n\nvar coordinates = (function* () {\n    var coord = {current: {x: api.randomCoord(), y: 0}};\n    while (!coord.previous || api.isWithinLimits(coord.previous)) {\n        yield coord;\n        coord = {\n            current: api.directions.Down(coord.current),\n            previous: coord.current\n        };\n    }\n});\n\n// taking full advantage of the generator function is not possible\n// with Rx.Observable.from([generator])\n// this is because rxjs does not support reactive pull without buffering\nvar droplets = Rx.Observable.interval(spawnPulse)\n    .map(coordinates)\n    .flatMap(coord => Rx.Observable.interval(fallPulse)\n        .map(() => coord.next())\n        .takeWhile(e => !e.done)\n        .pluck(\'value\'));\n\ndroplets.subscribe(coord => {\n    if (api.isWithinLimits(coord.current)) layer.fill(coord.current);\n    if (coord.previous) layer.clear(coord.previous);\n});';});


define('text!samples/rain-using-generator/README.md',[],function () { return 'This snippet makes particles fall from the sky as in the\n[Meteorites game](https://julienmoumne.github.io/rx-training-games/#?title=meteorites).\n\nThis approach leverages\n[ECMAScript 6 Generators](https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Statements/function%2A)\nand does not rely on on shared state.\n\nIt revolves around the following structure : \n```javascript\n{\n    previous : { x: xValue, y: yValue },\n    next : { x: xValue, y: yValue }\n}\n```';});


